import React, {Component} from 'react';

class Hello extends Component{
    static defaultProps = {
        name: '이름없음'
    };
    render(){
        const {color, name, isSpecial} = this.props;
        return(
            <>
            <div>Hello World!</div>
            <div style={{color:color}}>
                {isSpecial && <b>**</b>}
                My name is {name}</div>
            </>
        );
    }
}

export default Hello;
